import React, {useState} from 'react';
import "./Modal.css";

export default function Modal({obj}) {
    const [head, setHead] = useState(obj.title);
    const [body, setBody] = useState(obj.desc);

    return (
        <div className="tile">
            <div className="top">
                <h3>{head}</h3>
            </div>
            <div className="body">
                <p>{body}</p>
            </div>
        </div>
    )
}
