import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";
import { proj_feat, proj_pers, proj_acad, exp_it, exp_swe, education, lang } from "../../data"
import ExpList from '../ExpList/ExpList';

const expPerPage = 4;

export default function Career() {

    // CareerList hooks & states
    const [selectedProject, setSelectedProject] = useState("featured");
    const [selectedExperience, setSelectedExperience] = useState("all");
    const [projectData, setProjectData] = useState([]);
    const [expData, setExpData] = useState([]);

    // yes
    const [expToShow, setExpToShow] = useState([]);
    const [next, setNext] = useState(4);

    const showNext = (data, start, end) => {
        console.log(expToShow);
        setExpToShow(data.slice(start, end));
    };

    const handleShowMoreExp = () => {
        console.log(expToShow);
        showNext(projectData, next, next + expPerPage);
        setNext(next + expPerPage);
    };

    const handleFilterChange = () => {
        setNext(expPerPage);
        console.log(next);
        showNext(projectData, 0, next);
        setNext(next + expPerPage);
    }

    const projectsList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "featured",
            name: "Featured"
        },
        {
            id: "academic",
            name: "Academic"
        },
        {
            id: "personal",
            name: "Personal"
        }
    ]

    const experienceList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "swe",
            name: "Software Engineering"
        },
        {
            id: "it",
            name: "IT"
        }
    ]

    useEffect(() => {
        switch(selectedProject) {
            case "featured":
                setProjectData(proj_feat);
                break;
            case "academic":
                setProjectData(proj_acad);
                break;
            case "personal":
                setProjectData(proj_pers);
                break;
            default:
                setProjectData(proj_pers.concat(proj_acad));
                break;
        }
        switch(selectedExperience) {
            case "swe":
                setExpData(exp_swe);
                break;
            case "it":
                setExpData(exp_it);
                break;
            default:
                setExpData(exp_swe.concat(exp_it));
                break;
        }
    }, [selectedExperience, selectedProject])

    useEffect(() => {
        handleFilterChange();
        showNext(projectData, 0, next);
    }, [selectedProject])


    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <div className="container">
                <div className="left">
                    <h2 className="title">Projects</h2>
                    <ul>
                        {projectsList.map((item, index)=>(
                            <CareerList index={index} key={item.id} id={item.id} name={item.name} active={selectedProject === item.id} setSelected={setSelectedProject}></CareerList>
                        ))}
                    </ul>
                    <div className="career-items">
                        {expToShow.map((item) => (
                            <div key={item.id} className="item"> 
                                <a href={item.link} target="_blank"><img src={d.img} alt={d.desc}></img></a>
                                <div className="overlay">
                                    <h3 id="name">{d.title}</h3>
                                </div>
                            </div>
                        ))}
                        <button active={next === projectData.length} onClick={handleShowMoreExp}>Load more</button>
                    </div>
                </div>
                <div className="right">
                    <h2 className="title">Experience</h2>
                    <ul>
                        {experienceList.map((item, index)=>(
                            <CareerList index={index} key={item.id} id={item.id} name={item.name} active={selectedExperience === item.id} setSelected={setSelectedExperience}></CareerList>
                        ))}
                    </ul>
                    <div className="career-items">
                        {expData.map((d) => (
                            <div key={d.id} className="item"> 
                                <a href={d.link} target="_blank"><img src={d.img} alt={d.desc}></img></a>
                                <div className="overlay">
                                    <h3 id="name">{d.title}</h3>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
            
        </div>
    )
}
