import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";

import { featWorks, edu, proj, exp } from "../../data"

export default function Career() {

    const [selected, setSelected] = useState("top");
    const [data, setData] = useState([]);

    const list = [
        {
            id: "top",
            name: "Top"
        },
        {
            id: "education",
            name: "Education"
        },
        {
            id: "projects",
            name: "Projects"
        },
        {
            id: "experience",
            name: "Experience"
        }
    ]

    useEffect(() => {
        switch(selected) {
            case "top":
                setData(featWorks);
                break;
            case "education":
                setData(edu);
                break;
            case "projects":
                setData(proj);
                break;
            case "experience":
                setData(exp);
                break;
            default:
                setData(featWorks);
                break;
            
        }
    })


    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <ul>
                {list.map(item=>(
                    <CareerList id={item.id} name={item.name} active={selected === item.id} setSelected={setSelected}></CareerList>
                ))}
            </ul>
            <div className="career-items">
                {data.map((d) => (
                <div className="item" onClick={switchData()}> 
                    <a href={d.link}><img src={d.img} alt={d.desc}></img></a>
                    <h3 id="name" className="sitem active ">{d.name}</h3>
                    <h3 id="desc" className="sitem ">{d.desc}</h3>
                </div>
                ))}
            </div>
        </div>
    )
}
