import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";

import { proj_feat, proj_pers, proj_acad, exp_it, exp_swe } from "../../data"

export default function Career() {

    // CareerList hooks & states
    const [selectedProject, setSelectedProject] = useState("featured");
    const [projectData, setProjectData] = useState([]);
    const [selectedExperience, setSelectedExperience] = useState("all");
    const [expData, setExpData] = useState([]);

    // Popup modals TBA

    const projectsList = [
        {
            id: "featured",
            name: "Featured"
        },
        {
            id: "academic",
            name: "Academic"
        },
        {
            id: "personal",
            name: "Personal"
        },
        {
            id: "all",
            name: "All"
        }
    ]

    const experienceList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "swe",
            name: "Software Engineering"
        },
        {
            id: "it",
            name: "IT"
        }
    ]

    useEffect(() => {
        switch(selectedProject) {
            case "featured":
                setProjectData(proj_feat);
                break;
            case "academic":
                setProjectData(proj_acad);
                break;
            case "personal":
                setProjectData(proj_pers);
                break;
            default:
                setProjectData(proj_pers.concat(proj_acad));
                break;
        }
        switch(selectedExperience) {
            case "swe":
                setExpData(exp_swe);
                break;
            case "it":
                setExpData(exp_it);
                break;
            default:
                setExpData(exp_swe.concat(exp_it));
                break;
            
        }
    })


    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <div className="left">
                <h2 className="projects">Projects</h2>
                <ul>
                        {list.map(item=>(
                            <CareerList id={item.id} name={item.name} active={selected === item.id} setSelected={setSelectedProject}></CareerList>
                        ))}
                </ul>
                <div className="career-items">
                    {data.map((d) => (
                        <div className="item"> 
                            <a href={d.link} target="_blank"><img src={d.img} alt={d.desc}></img></a>
                            <h3 id="name" className="sitem active ">{d.name}</h3>
                        </div>
                    ))}
            </div>
            </div>
            <div className="right">
                <h2 className="experience">
                    <ul>
                        {list.map(item=>(
                            <CareerList id={item.id} name={item.name} active={selected === item.id} setSelected={setSelectedExperience}></CareerList>
                        ))}
                    </ul>
                </h2>
            </div>
            
        </div>
    )
}
