import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";

import { proj_feat, proj_pers, proj_acad, exp_it, exp_swe } from "../../data"

export default function Career() {

    // CareerList hooks & states
    const [selectedProject, setSelectedProject] = useState("featured");
    const [projectData, setProjectData] = useState([]);
    const [selectedExperience, setSelectedExperience] = useState("all");
    const [expData, setExpData] = useState([]);
    const [projects, setProjects] = useState([]);
    const [exp, setExp] = useState([]);

    // Popup modals TBA

    const projectsList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "featured",
            name: "Featured"
        },
        {
            id: "academic",
            name: "Academic"
        },
        {
            id: "personal",
            name: "Personal"
        }
    ]

    const experienceList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "swe",
            name: "Software Engineering"
        },
        {
            id: "it",
            name: "IT"
        }
    ]

    useEffect(() => {
        switch(selectedProject) {
            case "featured":
                setProjectData(proj_feat);
                break;
            case "academic":
                setProjectData(proj_acad);
                break;
            case "personal":
                setProjectData(proj_pers);
                break;
            default:
                setProjectData(proj_pers.concat(proj_acad));
                break;
        }
        switch(selectedExperience) {
            case "swe":
                setExpData(exp_swe);
                break;
            case "it":
                setExpData(exp_it);
                break;
            default:
                setExpData(exp_swe.concat(exp_it));
                break;
            
        }
        setProjects(projectData.slice(0, 4));
        setExp(expData.slice(0, 4))
    })

    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <div className="container">
                <div className="left">
                    <h2 className="title">Projects</h2>
                    <ul>
                        {projects.map(item=>(
                            <CareerList id={item.id} name={item.name} active={selectedProject === item.id} setSelected={setSelectedProject}></CareerList>
                        ))}
                    </ul>
                    <div className="career-items">
                        {projectData.map((d) => (
                            <div className="item"> 
                                <a href={d.link} target="_blank"><img src={d.img} alt={d.desc}></img></a>
                                <h3 id="name">{d.title}</h3>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="right">
                    <h2 className="title">Experience</h2>
                    <ul>
                        {experienceList.map(item=>(
                            <CareerList id={item.id} name={item.name} active={selectedExperience === item.id} setSelected={setSelectedExperience}></CareerList>
                        ))}
                    </ul>
                    <div className="career-items">
                        {exp.map((d) => (
                            <div className="item"> 
                                <a href={d.link} target="_blank"><img src={d.img} alt={d.desc}></img></a>
                                <h3 id="name">{d.title}</h3>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}
