import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";

import { featWorks, edu, proj, exp } from "../../data"

export default function Career() {

    // CareerList hooks & states
    const [selectedProjects, setSelectedProjects] = useState("featured");
    const [projectData, setProjectData] = useState([]);
    const [selectedExperience, setSelectedExperience] = useState("featured");
    const [expData, setExpData] = useState([]);

    // Popup modals TBA

    const projectsList = [
        {
            id: "featured",
            name: "Featured"
        },
        {
            id: "academic",
            name: "Academic"
        },
        {
            id: "personal",
            name: "Personal"
        },
        {
            id: "all",
            name: "All"
        }
    ]

    const experienceList = [
        {
            id: "all",
            name: "All"
        },
        {
            id: "swe",
            name: "Software Engineering"
        },
        {
            id: "it",
            name: "IT"
        }
    ]

    useEffect(() => {
        switch(selectedProjects) {
            case "featured":
                setProjectData
                break;
            case "academic":
                setData(edu);
                break;
            case "personal":
                setData(proj);
                break;
            case "all":
                setData(exp);
                break;
            default:
                setData(featWorks);
                break;
            
        }
        switch(selectedProjects) {
            case "top":
                setProjectData
                break;
            case "education":
                setData(edu);
                break;
            case "projects":
                setData(proj);
                break;
            case "experience":
                setData(exp);
                break;
            default:
                setData(featWorks);
                break;
            
        }
    })


    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <div className="left">
                <h2 className="projects">

                </h2>
            </div>
            <div className="right">
                <h2 className="experience">

                </h2>
            </div>
