import React from 'react'
import {useState, useEffect} from 'react';
import CareerList from '../CareerList/CareerList';
import "./Career.css";

import { featWorks, edu, proj, exp } from "../../data"

export default function Career() {

    const [selected, setSelected] = useState("top");
    const [data, setData] = useState([]);
    
    function switchData() {
        if (document.getElementById('name')) {
            if (document.getElementById('name').style.display == 'none') {
                document.getElementById('name').style.display = 'block';
                document.getElementById('desc').style.display = 'none';
            }
            else {
                document.getElementById('name').style.display = 'none';
                document.getElementById('desc').style.display = 'block';
            }
        }
    }

    const list = [
        {
            id: "top",
            name: "Top"
        },
        {
            id: "education",
            name: "Education"
        },
        {
            id: "projects",
            name: "Projects"
        },
        {
            id: "experience",
            name: "Experience"
        }
    ]

    useEffect(() => {
        switch(selected) {
            case "top":
                setData(featWorks);
                break;
            case "education":
                setData(edu);
                break;
            case "projects":
                setData(proj);
                break;
            case "experience":
                setData(exp);
                break;
            default:
                setData(featWorks);
                break;
            
        }
    })


    return (
        <div className="career" id="career">
            <h1>Career</h1>
            <ul>
                {list.map(item=>(
                    <CareerList id={item.id} name={item.name} active={selected === item.id} setSelected={setSelected}></CareerList>
                ))}
            </ul>
            <div className="career-items">
                {data.map((d) => (
                <div className="item"> 
                    <a href={d.link}><img src={d.img} alt={d.desc} onClick={switchData()}></img></a>
                    <h3 id="name" className="active">{d.name}</h3>
                    <h3 id="desc" className="">{d.desc}</h3>
                </div>
                ))}
            </div>
        </div>
    )
}
